//	new(access_token,refresh_token,fileId,type,role,value)
//	update(access_token,refresh_token,fileId,shareId,type,role,value)
//	remove(access_token,refresh_token,fileId,shareId)
// share
var Share = function(provider){this.provider = provider;};
Share.prototype = {
	list: function(fileId){
		return this.provider.share.list(this.tokens.access_token,this.tokens.refresh_token,fileId);
	},

	get: function(fileId,shareId){
		return this.provider.share.get(this.tokens.access_token,this.tokens.refresh_token,fileId,shareId);
	},

	new: function(fileId,type,role,value){
		return this.provider.share.new(this.tokens.access_token,this.tokens.refresh_token,fileId,type,role,value);
	},

	update: function(fileId,shareId,type,role,value){
		return this.provider.share.update(this.tokens.access_token,this.tokens.refresh_token,fileId,shareId,type,role,value);
	},

	remove: function(fileId,shareId){
		return this.provider.share.remove(this.tokens.access_token,this.tokens.refresh_token,fileId,shareId);
	},
};

module.exports = Client;
