var filefog = require('filefog');
var config = require('../config').dropbox;

filefog.use('googledrive',require('filefog-google'),{
	client_key : config.client_key,
	client_secret : config.client_secret,
	scope : config.scope,
	redirect_url: config.redirect_url,
	approval_prompt: 'force'
});

module.exports.provider = filefog.provider('googledrive');
module.exports.client = function(access_token, refresh_token){
	return filefog.client('googledrive',{
		access_token: access_token,
		refresh_token: refresh_token
	});
};