module.exports.get = function(access_token,refresh_token,fileId){
	return filefog.client(access_token,refresh_token)
	.then(function (client) {
		return client.getFileInformation(location);
	}).then(function(result){
		result.success = true;
		if(result._raw.type == 'album'){
			result.is_file = false;
			result.is_folder = true;
		}
		return result;
	}).catch(function (err){
		console.log(err);
		var result = {
			success: false,
			msg: 'one drive error'
		};
		return result;
	});
};

module.exports.listFile = function(access_token,refresh_token,fileId){
	return filefog.client(access_token,refresh_token)
	.then(function (client) {
		return client.retrieveFolderItems(location);
	}).then(function(result){
		result.success = true;
		result.content = result.content.map(function(file){
			if(file._raw.type == 'album'){
				file.is_file = false;
				file.is_folder = true;
			}
			return file;
		});
		return result;
	}).catch(function (err){
		console.log(err);
		var result = {
			success: false,
			msg: 'one drive error'
		};
		return result;
	});
};

